import React from 'react';
import { useEffect, useState } from "react";
import { Form, Link, useNavigate, useParams } from "react-router-dom";
import axios from 'axios';
import "../../../css/courseList.scss"
import "../../../css/base.css"
import "../../../css/main.css"
import "../../../css/seller.css"
import "../../../css/profile.css"
import "../../../css/product.css"
import "../../../css/product_detail.css"
import "../../../css/register.css";
import ReactPaginate from 'react-paginate';

export function UserUpdate() {
    const navigate = useNavigate();
    var param = useParams();
    const [totalPage, setTotalPage] = useState([]);
    const [courses, setCourses] = useState([]);
    const [user, setUser] = useState([]);
    const [detailCourses, setDetailCourses] = useState([]);

    const [course, setCourse] = useState([]);
    const [detailCourse, setDetailCourse] = useState([]);
    const [reviews, setReview] = useState([]);
    const [totalUsers, setTotalUser] = useState([]);

    function getCourse() {

        fetch("http://localhost:4000/Courses/" + param.id)
            .then(reponse => {
                if (reponse.ok) {
                    return reponse.json();
                }
            })
            .then(data => {
                setCourse(data);
            });
    }

    function getCourseDetail() {
        fetch(`http://localhost:4000/getCourseDetail?course_id=${param.id}`)
            .then(response => {
                if (response.ok) {
                    return response.json();
                }
                throw new Error('Network response was not ok.');
            })
            .then(data => {
                setDetailCourse(data);
            })
            .catch(error => {
                console.error('There was a problem with your fetch operation:', error);
            });
    }

    async function HandleSubmit(event) {
        event.preventDefault();
        const formData = new FormData(event.target);
        const courseFormData = new FormData();

        await fetch(`http://localhost:4000/user/${param.id}`, {
            method: "PATCH",
            body: formData
        });
        navigate("/admin/users");
    }
    



    //hook nay goi phuong thuc get lay ra course va detail course 
    //hook se duoc chay truoc
    useEffect(() => {
        axios.get(`http://localhost:4000/user/${param.id}`)
            .then(response => {
                console.log(response);
                setUser(response.data);
            })

        axios.get('http://localhost:4000/ScoreAndReview')
            .then(response => {
                setReview(response.data);
            })
            axios.get('http://localhost:4000/Courses')
            .then(response => {
                setCourses(response.data);
            })



        axios.get('http://localhost:4000/detailCourse')
            .then(response => {
                setDetailCourses(response.data);
            })
        getCourse()
        getCourseDetail();
    }, []);

    //sau khi hook truyen du lieu cho useState -> useState se co gia tri de tra xuong day


    const joinedDatas = courses.map(course => ({
        ...course,//chuyen doi gia tri -> giong ajax
        details: detailCourses.filter(detailCourse => detailCourse.course_id === course.id)
    }));


    //sau khi hook truyen du lieu cho useState -> useState se co gia tri de tra xuong day
    // const joinedData = detailCourse.filter(detailCourse => detailCourse.course_id === user.id_course)

    

    return (
        // <div >{
        //     <div class="register__course">
        //         <form onSubmit={HandleSubmit}>
        //             <h3 class="title__register">Đăng ký nhận tư vấn</h3>
        //             <p>Vui lòng điền đầy đủ các thông tin theo mẫu dưới đây,</p>
        //             <p>Anh Ngữ Thiên Ân sẽ liên lạc với bạn trong 1 – 2 ngày làm việc. Hoặc gọi ngay cho Trung tâm theo
        //                 số điện thoại bên dưới</p>
        //             <div class="body__register">
        //                 <input defaultValue={user.name} name="name" type="text" class="name__register" placeholder="Họ tên" />
        //                 <input defaultValue={user.email} name="email" type="text" class="email__register" placeholder="Email" />
        //                 <input defaultValue={user.phone} name="phone" type="text" class="phone__register" placeholder="Số điện thoại" />
        //                 <select name="id_course" id="select__course">
        //                     Chọn khóa học
        //                     {joinedDatas.map(course => (
        //                         String(course.id) === String(param.id) ? (
        //                             <option selected value={course.id}>{course.course_name}</option>
        //                         ) : <option value={course.id}>{course.course_name}</option>
        //                     ))
        //                     }
        //                 </select>
        //                 <textarea defaultValue={user.requirement} name="requirement" type="text" class="text__register" placeholder="Nội dung bạn cần tư vấn" ></textarea>
        //             </div>
        //             <div class="request">
        //                 <button type="submit" class="btn btn__request">Gửi yêu cầu</button>
        //             </div>
        //         </form>
                
        //     </div>
        // }
        // </div>

        <form onSubmit={HandleSubmitUpdateLect}>
         <div className="app__container">

                <div className="grid">
                    <div className="grid__row app__contents_seller">
                        <div className="gird__column-2_seller">
                            <nav className="category">
                                <ul className="category-list">
                                    <li className="category-item category-item--active">
                                        <a href="" className="category-item__link">Thông tin cơ bản</a>
                                    </li>
                                    <li className="category-item">
                                        <a href="" className="category-item__link">Thông tin khác</a>
                                    </li>
                                </ul>
                            </nav>
                        </div>
                        <div className="grid__column-10">
                            <div className="home-filter">
                                <span className="home-filter-title product-filter-title">Thêm Giảng Viên</span>
                            </div>

                            <div className="home__product">
                                <div className="grid__row-product">
                                    <div className="product__item-form">
                                        <label>Thêm ảnh khóa học</label>
                                        <button 
                                            className="btn--textFile have-img" 
                                            type="button"
                                            onClick={() => document.getElementById('ipImg').click()}
                                        >
                                            <div className="delete__img">
                                                <i className="fa-solid fa-x"></i>
                                            </div>
                                            <img src="http://localhost:4000/images/product1.jpg" alt=""/>
                                        </button>
                                        <button id="btnImg" className="btn--textFile no-img" type="button">
                                            <img id="main-imgPr" alt="" name="lecturer_name" src="http://localhost:4000/images/themanh.jpg"/>
                                            <span id="textBtnImg">Thêm ảnh</span>  
                                        </button>
                                        <input id="course_img" type="file" name="lecturer_img" />
                                    </div>
                                    <div className="product__item-form">
                                        <label htmlFor="lecturer_name">Tên Giảng Viên</label>
                                        <input defaultValue={Lecturer.lecturer_name} className="product__name-form" type="text" name="lecturer_name" placeholder="Nhập vào"/>
                                    </div>
                                  
                                    
                                    {
            Lecturer.lecturer_certificate && Lecturer.lecturer_certificate.split(",").map((lec, index) => (
                <div className="product__item-form" key={index}>
                    <label htmlFor="lecturer_name">{`Thêm chứng chỉ ${index}`}</label>
                 <input defaultValue={lec.trim()} className="product__name-form" type="text" name={`lecturer_certificate${index}`} placeholder="Nhập vào"/>
            </div>
            ))
            }


                                    <div className="product__item-form">
                                        <label htmlFor="lecturer_description">Mô tả Giảng Viên</label>
                                        <textarea defaultValue={Lecturer.lecturer_description} className="product__des-form" name="lecturer_description" placeholder="Nhập vào"></textarea>
                                    </div>
                                    <div className="home__product-btn">
                                        <button className="btn" type="button">Hủy</button>
                                        <button type="submit" className="btn btn--primary">Lưu</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
        </div>
       </form>
    );
}
